mapper.select:
select * from announcement a inner join company c on a.company_id = c.id WHERE company_fullname LIKE CONCAT( '%', #{content}, '%' ) OR announcement_title LIKE CONCAT( '%', #{content}, '%' ) OR company_vision LIKE CONCAT( '%', #{content}, '%' ) OR announcement_hire_type LIKE CONCAT( '%', #{content}, '%' ) OR announcement_salary LIKE CONCAT( '%', #{content}, '%' ) OR announcement_area LIKE CONCAT( '%', #{content}, '%' ) OR announcement_carrer LIKE CONCAT( '%', #{content}, '%' ) OR announcement_content LIKE CONCAT( '%', #{content}, '%' ) ORDER BY
1 DESC
#{stackId}
DESC
LIMIT #{start}, #{end}
